// popup position fix
function initPopupPositionFix() {
	var leftSideClass = 'left';
	var rightSideClass = 'right';
	var wrap = jQuery('#wrapper');
	var extraOffset = 50;

	jQuery('.bubble').each(function() {
		var bubble = jQuery(this);
		var popup = bubble.find('.popup-block');
		var timer, offset, width, wrapWidth;

		jQuery(window).on('load resize orientationchange', setClass);
		setClass();

		function setClass() {
			clearTimeout(timer);

			timer = setTimeout(function() {
				bubble.removeClass(rightSideClass).removeClass(leftSideClass);
				offset = popup.offset().left;
				width = popup.outerWidth();
				wrapWidth = wrap.innerWidth();

				if (offset < 0 + extraOffset){
					bubble.removeClass(rightSideClass).addClass(leftSideClass);
				} else if (offset + width + extraOffset > wrapWidth) {
					bubble.addClass(rightSideClass).removeClass(leftSideClass);
				} else {
					bubble.removeClass(rightSideClass).removeClass(leftSideClass);
				}
			}, 200);
		}
	});
}

// init video bg
function initVimeoVideo() {
	var activeClass = 'video-playing';

	jQuery('.vimeo-video-holder').each(function(i) {
		var holder = jQuery(this).removeClass(activeClass);
		var btnPlay = holder.find('.btn-watch');
		var btnClose = holder.find('.btn-close');
		var iframe = holder.find('iframe');
		var videoId = !iframe.attr('id') ? 'iframeid-' + i : iframe.attr('id');
		var iframeSrc = iframe.attr('src');
		var playerOrigin = '*';
		var scrollTimer;

		iframe.attr({
			wmode: 'transparent',
			id: videoId,
			src: iframeSrc + (iframeSrc.indexOf('?') === -1 ? '?' : '&') + 'api=1' + '&player_id=' + videoId + '&wmode=opaque'
		});

		var player = iframe[0];

		$f(player).addEvent('ready', ready);

		var froogaloop;

		function addEvent(element, eventName, callback) {
			if (element.addEventListener) {
				element.addEventListener(eventName, callback, false);
			} else {
				element.attachEvent('on' + eventName, callback);
			}
		}

		function ready(playerId) {
			froogaloop = $f(playerId);

			btnPlay.on('click', playHandler);
			btnClose.on('click', pauseHandler);

			function playHandler(e) {
				e.preventDefault();
				if (!holder.hasClass(activeClass)) {
					holder.addClass(activeClass);
					froogaloop.api('play');
					scrollTop();
				}
			}

			function pauseHandler(e) {
				e.preventDefault();
				if (holder.hasClass(activeClass)) {
					holder.removeClass(activeClass);
					froogaloop.api('pause');
				}
			}

			function scrollTop() {
				clearTimeout(scrollTimer);

				scrollTimer = setTimeout(function() {
					jQuery('html, body').stop().animate({
						scrollTop: holder.offset().top
					}, 600);
				}, 200);
			}
		}
	});
}

// init fixed header
function initFixedHeader() {
	var win = jQuery(window);
	var winTop = win.scrollTop();
	var activeClass = 'fixed-header';

	jQuery('#header .header-holder').each(function() {
		var header = jQuery(this);
		var extraOffset = 0;
		var offset = header.offset().top - (header.css('position') === 'fixed' ? win.scrollTop() - extraOffset : 0);
		//var animElem = jQuery('<div class="bg"></div>').css('opacity', 0);
		var distance = 70;
		var isFixed = false;
		var lastScrollTop = 0
		var delta = 20;
		// because of announcement banner
		if ($('.home-announcement').length){
			delta = 40;
		}
		//if (!IsTouchDeviceDetect)  animElem.appendTo(header);

		resizeHandler();
		win.on('resize orientationchange load', resizeHandler);
		win.on('scroll touchmove', scrollHandler);

		function scrollHandler() {

		       var st = win.scrollTop();
		       
		       if(Math.abs(lastScrollTop - st) <= delta)
		          return;
		       
		       if (st > lastScrollTop || st <= delta){
		           // downscroll code
					header.addClass("hidden");
					isFixed = false;

					if (st < delta){
						header.removeClass(activeClass).removeClass("hidden");
						isFixed = false;
					}
		       } else {
			          // upscroll code
					if (!isFixed && st > distance){
						header.removeClass("hidden").addClass(activeClass);
						isFixed = true;
					}
		       }
		       lastScrollTop = st;
			/*var lastScrollTop = 0;
			$(window).scroll(function(event){
			   var st = $(this).scrollTop();
			   if (st > lastScrollTop){
			       // downscroll code
			       console.log(st + " " + lastScrollTop);
				if (isFixed){
					header.removeClass(activeClass);
					isFixed = false;
				}
			   } else {
			      // upscroll code
				if (!isFixed){
					header.addClass(activeClass);
					isFixed = true;
				}
			      			   console.log('upscroll');
			   }
			   lastScrollTop = st;
			   if (!IsTouchDeviceDetect) animateElem();
			});*/
			/*winTop = win.scrollTop();
			if (winTop > offset){
				if (!isFixed){
					header.addClass(activeClass);
					isFixed = true;
				}
			} else {
				if (isFixed){
					header.removeClass(activeClass);
					isFixed = false;
				}
			}
			if (!IsTouchDeviceDetect) animateElem();*/
		}

		function animateElem() {
			if (winTop > offset){
				var koef = 1 / distance;
				var diff = winTop - offset;
				var opacity = Math.min(koef * diff, 1);
				animElem.css('opacity', opacity);
			} else {
				animElem.css('opacity', 0);
			}
		}

		function resizeHandler() {
			winTop = win.scrollTop();
			header.removeClass(activeClass);
			offset = header.offset().top - (header.css('position') === 'fixed' ? winTop - extraOffset : 0);
			isFixed = false;
			scrollHandler();
		}
	});
}

