// fade gallery init
function initSlideShow() {
	jQuery('.custom-slideshow').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.desktop-pagination li',
		event: 'click',
		useSwipe: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500,
		onInit: function(self) {
			self.addPagerHolder = self.gallery.find('.pagination');
			if (self.addPagerHolder.length) {
				self.pagerLinks.each(function(i) {
					var link = jQuery(this);
					self.addPagerHolder.empty();
					self.addPagerList = jQuery(self.options.pagerList).appendTo(self.addPagerHolder);
					for(var i = 0; i < self.stepsCount; i++) {
						jQuery(self.options.pagerListItem).appendTo(self.addPagerList).find(self.options.pagerListItemText).text(i+1);
					}
					self.addPagerLinks = self.addPagerList.children();
				});
				if(self.addPagerLinks.length) {
					self.refreshPagerState = function() {
						self.addPagerLinks.removeClass(self.options.activeClass).eq(self.currentIndex).addClass(self.options.activeClass);
					}
					self.addPagerLinksHandler = function(e) {
						e.preventDefault();
						self.numSlide(self.addPagerLinks.index(e.currentTarget));
						if(self.options.autorotationStopAfterClick) {
							self.stopRotation();
						}
					};
					self.addPagerLinks.bind(self.options.event, self.addPagerLinksHandler);
					self.refreshPagerState();
				}
			}
		},
		onBeforeChange: function(self) {
			if(self.addPagerLinks.length) self.refreshPagerState();
		}
	});
	jQuery('div.slideshow').fadeGallery({
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		event: 'click',
		useSwipe: true,
		autoRotation: false,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
	
}

// mobile menu init
function initMobileNav() {
	// handle layout resize
	ResponsiveHelper.addRange({
		'..1024': {
			on: function() {
				jQuery('html').mobileNav({
					menuActiveClass: 'nav-active',
					menuOpener: '.nav-opener',
					onBeforeShow: function(self) {
						self.scrollTop = jQuery(window).scrollTop();
						jQuery('html').css({
							'top': -this.scrollTop
						});
					},
					onHide: function(self) {
						jQuery('html').css({
							'top': ''
						});
						jQuery(window).scrollTop(self.scrollTop);
					}
				});
			},
			off: function() {
				jQuery('html').each(function() {
					var inst = jQuery(this).data('MobileNav');
					if (inst) {
						var scrollTop = inst.scrollTop;
						inst.destroy();
						jQuery('html').css({
							'top': ''
						});
						jQuery(window).scrollTop(scrollTop);
					}
				});
			}
		}
	});
}

