// detect device type
IsTouchDeviceDetect = (function() {
	var isTouchDevice = /Windows Phone/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	if (isTouchDevice) {
		jQuery('body').addClass('touch-device');
	} else {
		jQuery('body').addClass('desktop-device');
	}
	return isTouchDevice;
}());

/*
 * Browser Detect script
 */
BrowserDetect = (function() {
	// script settings
	var options = {
		osVersion: false,
		minorBrowserVersion: true
	};

	// browser data
	var browserData = {
		browsers: {
			edge: uaMatch(/ Edge\/([0-9\.]*)/),
			chrome: uaMatch(/Chrome\/([0-9\.]*)/),
			firefox: uaMatch(/Firefox\/([0-9\.]*)/),
			safari: uaMatch(/Version\/([0-9\.]*).*Safari/),
			opera: uaMatch(/Opera\/.*Version\/([0-9\.]*)/, /Opera\/([0-9\.]*)/),
			msie: uaMatch(/MSIE ([0-9\.]*)/, /Trident.*rv:([0-9\.]*)/)
		},
		engines: {
			edgehtml: uaContains(' Edge\/'),
			webkit: uaContains('AppleWebKit'),
			trident: uaMatch(/(MSIE|Trident)/),
			gecko: uaContains('Gecko'),
			presto: uaContains('Presto')
		},
		platforms: {
			win: uaMatch(/Windows NT ([0-9\.]*)/),
			mac: uaMatch(/Mac OS X ([0-9_\.]*)/),
			linux: uaContains('X11', 'Linux')
		}
	};

	// perform detection
	var ua = navigator.userAgent;
	var detectData = {
		platform: detectItem(browserData.platforms),
		browser: detectItem(browserData.browsers),
		engine: detectItem(browserData.engines)
	};

	// private functions
	function uaMatch() {
		var regs = Array.prototype.slice.apply(arguments);
		return function() {
			for (var i = 0, result; i < regs.length; i++) {
				result = regs[i].exec(ua);
				if (result) {
					return result[1];
				}
			}
		};
	}
	function uaContains(word) {
		var words = Array.prototype.slice.apply(arguments);
		return function() {
			for(var i = 0; i < words.length; i++) {
				if(ua.indexOf(words[i]) < 0) {
					return;
				}
			}
			return true;
		};
	}
	function detectItem(items) {
		var detectedItem = null, itemName, detectValue;
		for(itemName in items) {
			if(items.hasOwnProperty(itemName)) {
				detectValue = items[itemName]();
				if(detectValue) {
					return {
						name: itemName,
						value: detectValue
					};
				}
			}
		}
	}

	// add classes to root element
	(function() {
		// helper functions
		var addClass = function(cls) {
			var html = document.documentElement;
			html.className += (html.className ? ' ' : '') + cls;
		};
		var getVersion = function(ver) {
			return typeof ver === 'string' ? ver.replace(/\./g, '_') : 'unknown';
		};

		// add classes
		if(detectData.platform) {
			addClass(detectData.platform.name);
			if(options.osVersion) {
				addClass(detectData.platform.name + '-' + getVersion(detectData.platform.value));
			}
		}
		if(detectData.engine) {
			addClass(detectData.engine.name);
		}
		if(detectData.browser) {
			addClass(detectData.browser.name);
			addClass(detectData.browser.name + '-' + parseInt(detectData.browser.value, 10));
			if(options.minorBrowserVersion) {
				addClass(detectData.browser.name + '-' + getVersion(detectData.browser.value));
			}
		}
	}());

	// export detection information
	return detectData;
}());

/*
 * Responsive Layout helper
 */
ResponsiveHelper = (function($){
	// init variables
	var handlers = [],
		prevWinWidth,
		win = $(window),
		nativeMatchMedia = false;

	// detect match media support
	if(window.matchMedia) {
		if(window.Window && window.matchMedia === Window.prototype.matchMedia) {
			nativeMatchMedia = true;
		} else if(window.matchMedia.toString().indexOf('native') > -1) {
			nativeMatchMedia = true;
		}
	}

	// prepare resize handler
	function resizeHandler() {
		var winWidth = win.width();
		if(winWidth !== prevWinWidth) {
			prevWinWidth = winWidth;

			// loop through range groups
			$.each(handlers, function(index, rangeObject){
				// disable current active area if needed
				$.each(rangeObject.data, function(property, item) {
					if(item.currentActive && !matchRange(item.range[0], item.range[1])) {
						item.currentActive = false;
						if(typeof item.disableCallback === 'function') {
							item.disableCallback();
						}
					}
				});

				// enable areas that match current width
				$.each(rangeObject.data, function(property, item) {
					if(!item.currentActive && matchRange(item.range[0], item.range[1])) {
						// make callback
						item.currentActive = true;
						if(typeof item.enableCallback === 'function') {
							item.enableCallback();
						}
					}
				});
			});
		}
	}
	win.bind('load resize orientationchange', resizeHandler);

	// test range
	function matchRange(r1, r2) {
		var mediaQueryString = '';
		if(r1 > 0) {
			mediaQueryString += '(min-width: ' + r1 + 'px)';
		}
		if(r2 < Infinity) {
			mediaQueryString += (mediaQueryString ? ' and ' : '') + '(max-width: ' + r2 + 'px)';	
		}
		return matchQuery(mediaQueryString, r1, r2);
	}

	// media query function
	function matchQuery(query, r1, r2) {
		if(window.matchMedia && nativeMatchMedia) {
			return matchMedia(query).matches;
		} else if(window.styleMedia) {
			return styleMedia.matchMedium(query);
		} else if(window.media) {
			return media.matchMedium(query);
		} else {
			return prevWinWidth >= r1 && prevWinWidth <= r2;
		}
	}

	// range parser
	function parseRange(rangeStr) {
		var rangeData = rangeStr.split('..');
		var x1 = parseInt(rangeData[0], 10) || -Infinity;
		var x2 = parseInt(rangeData[1], 10) || Infinity;
		return [x1, x2].sort(function(a, b){
			return a - b;
		});
	}

	// export public functions
	return {
		addRange: function(ranges) {
			// parse data and add items to collection
			var result = {data:{}};
			$.each(ranges, function(property, data){
				result.data[property] = {
					range: parseRange(property),
					enableCallback: data.on,
					disableCallback: data.off
				};
			});
			handlers.push(result);

			// call resizeHandler to recalculate all events
			prevWinWidth = null;
			resizeHandler();
		}
	};
}(jQuery));

/*
 *VideoBG
 */
;(function($) {
	'use strict';

	function VideoBG(options) {
		this.options = $.extend({
			dataPoster: 'poster',
			video: 'video'
		}, options);
		this.init();
	}
	VideoBG.prototype = {
		init: function() {
			this.findElements();
		},
		findElements: function() {
			this.holder = $(this.options.holder);
			this.video = this.holder.find(this.options.video);
			if (!this.holder.length || !this.video.length) return;
			this.urlPoster = this.video.attr(this.options.dataPoster);
			if (this.urlPoster) {
				this.createPoster();
			}
			if (isTouchDevice) {
				this.video.remove();
			} else {
				this.resize();
			}
		},
		createPoster: function() {
			this.holder.css({
				'background-image': 'url(' + this.urlPoster + ')',
				'background-repeat':'no-repeat',
				'background-size':'cover'
			});
		},
		getRatio: function(video) {
			if (video.prop('naturalWidth')) {
				return video.prop('naturalWidth') / video.prop('naturalHeight');
			} else {
				return video[0].width / video[0].height;
			}
		},
		getDimensions: function(data) {
			// calculate element coords to fit in mask
			var ratio = data.videoRatio || (data.videoWidth / data.videoHeight),
				slideWidth = data.maskWidth,
				slideHeight = slideWidth / ratio;

			if (slideHeight < data.maskHeight) {
				slideHeight = data.maskHeight;
				slideWidth = slideHeight * ratio;
			}
			return {
				width: slideWidth,
				height: slideHeight,
				top: (data.maskHeight - slideHeight) / 2,
				left: (data.maskWidth - slideWidth) / 2
			};
		},
		resizeHandler: function() {
			this.resizeVideo(this.video, this.holder);
		},
		resizeVideo: function(video, container) {
			var styles = this.getDimensions({
				videoRatio: this.getRatio(video),
				maskWidth: container.outerWidth(),
				maskHeight: container.outerHeight()
			});
			video.css({
				marginTop: styles.top,
				marginLeft: styles.left,
				width: styles.width,
				height: styles.height
			}).attr({
				width: styles.width,
				height: styles.height
			});
		},
		resize: function() {
			// resize image
			this.resizeHandler = jQuery.proxy(this.resizeHandler, this);
			this.resizeHandler();
			$(window).on('load resize orientationchange', this.resizeHandler);
		}
	};

	var isTouchDevice = /Windows Phone/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	
	$.fn.videoBG = function(options) {
		return this.each(function() {
			if (!$(this).data('VideoBG')) {
				$(this).data('VideoBG', new VideoBG($.extend({holder: this}, options)));
			}
		});
	};
}(jQuery));

